mapper.select:

select lvl, inquiry_cd, parent_cd, group_cd, inquiry_title, rocket_cd, write_date, id
from (
	    select group_cd, lvl, inquiry_cd, parent_cd, inquiry_title, rocket_cd, write_date, id
	    from (
	            select  group_cd, level as lvl, inquiry_cd, parent_cd, inquiry_title, rocket_cd, write_date, id
				from (
				        select ti.INQUIRY_CD , ti.PARENT_CD , ti.GROUP_CD , ti.INQUIRY_TITLE , ti.INQUIRY_CONTENT , ti.ROCKET_CD , ti.WRITE_DATE , tri.ID 
						from T_INQUIRY ti JOIN T_ROCKET_INFO tri ON ti.ROCKET_CD = tri.ROCKET_CD
						where group_cd in (
											select group_cd
						                    from (
						                            SELECT ROWNUM as recNum, group_cd
						                            FROM (
															SELECT DISTINCT group_cd from T_INQUIRY
						                                    order by group_cd desc
						                                 )
						                         )  
						                    WHERE recnum BETWEEN (#{section} - 1) * 100 + (#{pageNum} - 1) * 10 + 1 AND (#{section} - 1) * 100 + #{pageNum} * 10
						                 )
				    )
				start with parent_cd = 0
				connect by prior inquiry_cd = parent_cd
				order siblings by write_date DESC
	         )   
	)

mapper.update:

UPDATE T_INQUIRY
SET inquiry_title = #{inquiry_title}, inquiry_content = #{inquiry_content}
WHERE inquiry_cd = #{inquiry_cd}

choose.when:
UPDATE T_INQUIRY_IMG
SET imageFileName = #{item.imageFileName}
WHERE inquiry_cd = #{item.inquiry_cd} AND inquiry_img_cd = #{item.inquiry_img_cd}

mapper.delete:

DELETE 
FROM T_INQUIRY_IMG
WHERE inquiry_img_cd = #{inquiry_img_cd} AND inquiry_cd = #{inquiry_cd}
